import React, { Component } from 'react';
class TaskForm
 extends React.Component {
  constructor(props) {
    super(props);
    this.state = { title: '' ,
    description :'',
    severity:''
  };
  }
  handleChange = (event) => {
    console.log(event, "event")
    this.setState({[event.target.title]: event.target.value},
      {[event.target.description]: event.target.value},
      {[event.target.severity]: event.target.value},
      );
  }
  onCreateTask = (event) => {
    fetch('http://localhost:8080/api/v1/tasks', {
        method: 'POST',
        // We convert the React state to JSON and send it as the POST body
        body: this.state
      }).then(function(response) {
        console.log(response)
        return response.json();
      });

    event.preventDefault();
}

    render() { 
        return (<div className="container">
        <form>
  <div class="form-group">
  <label for="title">Title</label>
    <input class="form-control" id="title" type="text" value={this.state.value} name="title" onChange={this.handleChange} />
  </div>
  <div class="form-group">
  <div className="container" />
    <label for="exampleFormControlSelect2">Severity</label>
    <select name="cars" id="severitylevel" className="severity-Block">
    <option value="volvo">1</option>
    <option value="saab">2</option>
    <option value="opel">3</option>
  </select>
  </div>
  <div class="form-group">
    <label for="exampleFormControlTextarea1">Description</label>
    <textarea class="form-control" id="exampleFormControlTextarea1" rows="3"></textarea>
  </div>
  <button type="submit" class="btn btn-primary">Submit</button>
</form>
        </div>  );
    }
}
 
export default TaskForm
;
