import React, { Component } from 'react';
class TaskForm
 extends Component {
  handleIncrement = counter => {
    var observe;
    if (window.attachEvent) {
        observe = function (element, event, handler) {
            element.attachEvent('on'+event, handler);
        };
    }
    else {
        observe = function (element, event, handler) {
            element.addEventListener(event, handler, false);
        };
    }
    function init () {
        var text = document.getElementById('text');
        function resize () {
            text.style.height = 'auto';
            text.style.height = text.scrollHeight+'px';
        }
        /* 0-timeout to get the already changed text */
        function delayedResize () {
            window.setTimeout(resize, 0);
        }
        observe(text, 'change',  resize);
        observe(text, 'cut',     delayedResize);
        observe(text, 'paste',   delayedResize);
        observe(text, 'drop',    delayedResize);
        observe(text, 'keydown', delayedResize);
    
        text.focus();
        text.select();
        resize();
    }

}
    render() { 
        return (<div className="container">
        <form>
  <div class="form-group">
  <label for="title">Title</label>
    <input class="form-control" id="title" rows="3"></input>
  </div>
  <div class="form-group">
  <div className="container" />
    <label for="exampleFormControlSelect2">Severity</label>
    <select name="cars" id="severitylevel" className="severity-Block">
    <option value="volvo">1</option>
    <option value="saab">2</option>
    <option value="opel">3</option>
  
  </select>
  </div>
  <div class="form-group">
    <label for="exampleFormControlTextarea1">Description</label>
    <textarea onload="init();" class="form-control" id="exampleFormControlTextarea1" rows="3"></textarea>
  </div>
  <button type="submit" class="btn btn-primary">Submit</button>
</form>
        </div>  );
    }
}
 
export default TaskForm
;
