import React from 'react';
import { Table,Container,Input,Button,Label, FormGroup, Form} from 'reactstrap';
class TaskForm
 extends React.Component {
  constructor(props) {
    super();
    this.state = { title: '' ,
    description :'',
    severity:''
  };
  }

  handleChange = (event) => {
    this.setState({[event.target.name]: event.target.value});
  }

handleSubmit = (event) => {
    const basePath = 'http://localhost:8080/api/v1/tasks';

  fetch(basePath, {
    method: 'POST',
    headers: {
      'Acept' :'application/json',
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(this.state)
  }).then(function(response) {
              console.log(response)
              return response;
            })
          event.preventDefault();
}

    render() { 
        return (<Div className="container">
         <Form onSubmit={this.handleSubmit}>
         <FormGroup>
            <Label name="titleField">Title</Label>
              <Input autoComplete="nope" className="form-control" id="title" type="text" value={this.state.value} name="title" onChange={this.handleChange} />
              </FormGroup>
              <FormGroup>
            <Div className="container" />
              <Label>Severity</Label>
                <Select id="severitylevel" className="severity-Block" name="severity" onChange={this.handleChange}>
                  <Option value="0">Low</Option>
                  <Option value="1">Medium</Option>
                  <Option value="2">High</Option>
                </Select>
            </FormGroup> 
            <FormGroup>
            
          <Label name="DescriptionLabel">Description</Label>
            <Textarea className="form-control" name="description" onChange={this.handleChange}></Textarea>
        </FormGroup> 
        <Button type="submit" className="btn btn-primary">Submit</Button>
      </Form>
    </Div> );
    }
}
 
export default TaskForm
;
