import React, {Component} from 'react';

class TaskList
    extends Component {
    state = {
        tasks: []
    };

    componentDidMount() {
        fetch('http://localhost:8080/api/v1/tasks')
            .then(res => res.json())
            .then((data) => {
                this.setState({tasks: data})
            })
            .catch(console.log)
    }
    onDelete(id){
        fetch('http://localhost:8080/api/v1/tasks/' + id, {
            method: 'DELETE',
        })
            .then(res => res.text()) // or res.json()
            .then(res => console.log(res))
    }

    render() {
        return (<div>
                <h1 align="center">Task List</h1>
                {this.state.tasks.map((task) => (
                    <div className="card"  key={task.id}>
                        <div className="card-body" >
                            <h5 className="card-title" >{task.title}</h5>
                            <h5 className="card-subtitle mb-2 text-muted" key={task.id}>{task.severity}</h5>
                            {/*<p className="card-text">{task.description}</p>*/}
                            {/*<h5 className="card-subtitle mb-2 text-muted">{task.creationDate}</h5>*/}
                            <button onClick={this.onDelete(task.id)}>Delete</button>
                        </div>
                    </div>
                ))}
            </div>
        );
    }
}

export default TaskList;
